mapper.select:

SELECT

FROM recoder
WHERE deleteFlag = 0 AND creator = #{creator}

    AND recoderType = #{recoderType}




            AND LOCATE(#{tag},tags)


            AND tags = ''




    AND (
      title LIKE #{keywordPattern}
      OR
      text LIKE #{keywordPattern}
    )

ORDER BY love DESC, lastModifyTime DESC
LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE recoder
SET
  lastModifyTime = NOW()

   , title = #{title}


    , content = #{content}


    , text = #{text}


    , love = #{love}


    , recoderType = #{recoderType}


    , tags = #{tags}

WHERE id = #{id}

UPDATE recoder
SET deleteFlag = 1
WHERE id = #{value}

UPDATE recoder
SET love = NOT love, lastModifyTime = NOW()
WHERE id=#{value}
